mapper.update:
update U_ALL_MENU


		code = #{code},


		status = #{status}


where id = #{id}

mapper.select:


SELECT id
     , pid
     , url
     , style
     , img
     , level
     , code
     , type
     , status
     , remark
     , creator
     , created
FROM U_ALL_MENU



	id = #{id}


	and pid = #{pid}


	and uid = #{uid}


	and code = #{code}


	and type = #{type}


	and status = #{status}



	SELECT m.id
	     , m.pid
	     , m.url
	     , m.style
	     , m.img
	     , m.level
	     , m.code
	     , m.type
	     , m.status
	     , m.remark
	     , m.creator
	     , m.created
	FROM
	  U_ALL_MENU m
	LEFT JOIN U_ALL_VIEW v
	ON v.mid = m.id



		v.uid = #{uid}


		AND m.status = #{status}


		AND m.type = #{type}


		AND ( m.pid = #{id}


	  OR m.pid IN (
		  SELECT m.id
		  FROM
		    U_ALL_MENU m
		  LEFT JOIN U_ALL_VIEW v
		  ON v.mid = m.id



			v.uid = #{uid}


			AND m.status = #{status}


			AND m.type = #{type}


			AND m.pid = #{id}

	 ) 

 ) 

ORDER BY m.sort ASC

SELECT m.id , m.pid , m.url , m.style , m.img , m.level , m.code , m.type , m.status , m.remark , m.creator , m.created FROM U_ALL_MENU m LEFT JOIN U_ALL_VIEW v ON v.mid = m.id
v.uid = #{uid}
AND m.status = #{status}
AND m.type = #{type}
AND ( m.pid = #{pid} or m.id = #{pid} )
ORDER BY m.sort ASC



			SELECT m.id
			     , m.pid
			     , m.url
 				 , CASE WHEN m.id = '51ff0d7d975f475110fe1100' THEN null ELSE m.style END style
			     , CASE WHEN m.id = '51ff0d7d975f475110fe1100' THEN 'Html/images/icons/0-1.png' ELSE m.img END img
			     , m.level
			     , m.code
			     , m.type
			     , m.status
			     , m.remark
			     , m.creator
			     , m.created
			FROM
			  U_ALL_MENU m
			LEFT JOIN U_ALL_VIEW v
			ON v.mid = m.id



				v.uid = #{uid}


				AND m.status = #{status}


				AND m.type = #{type}


				AND m.pid = #{id}


			  OR m.pid IN (
				  SELECT m.id
				  FROM
				    U_ALL_MENU m
				  LEFT JOIN U_ALL_VIEW v
				  ON v.mid = m.id



						v.uid = #{uid}


						AND m.status = #{status}


						AND m.type = #{type}


						AND m.pid = #{id}

				 )


				AND v.uid = #{uid}


				AND m.status = #{status}


		ORDER BY m.sort ASC
